import React,{useState} from 'react'
import Data from './Data';

const App = () => {
    const [data,setData] = useState(Data);
    return (
        <>
            <div className="container">
                    <h1 className="text-center text-capitalize mt-3">Order Your Item form the below list</h1>
                   <div className="row">
                       <div className="col">
                           <button className="col-2 offset-1 btn btn-outline-warning">Breakfast</button>
                           <button className="col-2 offset-1 btn btn-outline-warning">Lunch</button>
                           <button className="col-2 offset-1 btn btn-outline-warning">Dinner</button>
                           <button className="col-2 offset-1 btn btn-outline-warning">All</button>
                        </div>
                   </div>
                    <div className="row">
                        
                            {data.map((element,index)=>{
                                const {id,name,image,category,desc} = element;
                                return(
                                    <div className="col-lg-4 col-xm-12 col-md-6 my-1">
                                        <div className="card" key={id}>
                                            <img src={image} alt="poha pic" className="card-img zoom" width="200" height="200"/>
                                            <div className="card-body">
                                                <h2 className="card-title text-capitalize ">{name}</h2>
                                                <p className="card-text text-capitalize ">{desc}</p>
                                                <button className="btn btn-outline-warning ">Order</button>
                                            </div>
                                        </div>
                                    </div>
                                )
                            })
                           }
                     
                    </div>
            </div>
        </>
    )
}

export default App;
